import React from "react";
import {Link} from 'react-router-dom';
import PropTypes from "prop-types";

function Searchbar({id, year, title, poster, rating, director, actor}) {
  return (
    <div className="product">
    <a href={id} target="_blank">
      <img src={poster} alt={title} titlt={title}></img>
    <div className="product__data">
      <h3 className="product__title">{
          title.replace(/<b>/gi,"").replace(/<\/b>/gi,"")
        }</h3>
      <p className="product__rating">
        <span>평점</span> {rating}/10
      </p>
      <p className="product__year">
        <span>개봉일</span> {year}
      </p>
    <p className="movie__director">
      <span>감독</span> {director}
    </p>
    <p className="movie__actor">
      <span>배우</span> {actor}
    </p>
    </div>
  </a>
  </div>
  )
};

Searchbar.propTypes = {
  id: PropTypes.string.isRequired,
  year: PropTypes.string.isRequired,
  title: PropTypes.string.isRequired,
  poster: PropTypes.string.isRequired,
  rating: PropTypes.string.isRequired,
  director: PropTypes.string.isRequired,
  actor: PropTypes.string.isRequired

};

export default Searchbar;
